var app = require('./app.js');
var nodeHandler = require('./routes/node');

app.set('port', process.env.PORT || 3000);

exports.start=function(port)
{
	app.set('port', process.env.PORT || port);
	
	var server = app.listen(app.get('port'), function() {
		var host = server.address().address;
		console.log("Framework Called");
	});
};

exports.addNode=function(req, res)
{
	nodeHandler.addNode(req, res);
}

exports.viewAllNodes = function(req, res)
{
	nodeHandler.viewAllNodes(req, res);

}

exports.removeNode = function(req, res)
{
	nodeHandler.removeNode(req, res);
}

exports.viewNamedNodes = function(req, res)
{
	nodeHandler.viewNamedNodes(req, res);
}